<?php
namespace Packfire\Linq\Query;

/**
 * Test class for GroupBy.
 * Generated by PHPUnit on 2012-09-17 at 08:21:01.
 */
class GroupByTest extends \PHPUnit_Framework_TestCase
{
    /**
     * @var \Packfire\Linq\Query\GroupBy
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->object = new GroupBy(
            function ($row) {
                return $row['age'];
            }
        );
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
    }

    /**
     * @covers \Packfire\Linq\Query\GroupBy::run
     */
    public function testRun()
    {
        $data = array(
            array('name' => 'Susan', 'age' => 5),
            array('name' => 'Georgy', 'age' => 7),
            array('name' => 'Tommy', 'age' => 6),
            array('name' => 'Rina', 'age' => 5),
            array('name' => 'Harry', 'age' => 6)
        );
        $result = $this->object->run($data);
        $this->assertEquals(5, $result[0]->key());
        $this->assertEquals(7, $result[1]->key());
        $this->assertEquals(6, $result[2]->key());
    }
}
