<?php
namespace Packfire\Validator;

/**
 * Test class for CallbackValidator.
 * Generated by PHPUnit on 2012-09-23 at 01:21:50.
 */
class CallbackValidatorTest extends \PHPUnit_Framework_TestCase
{
    /**
     * @var \Packfire\Validator\CallbackValidator
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->object = new CallbackValidator(
            function ($value) {
                return $value === 5;
            }
        );
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
    }

    /**
     * @covers \Packfire\Validator\CallbackValidator::validate
     */
    public function testValidate()
    {
        $this->assertTrue($this->object->validate(5));
        $this->assertFalse($this->object->validate('5'));
    }
}
