<?php

pload('packfire.text.pNewline');

/**
 * Test class for pNewline.
 * Generated by PHPUnit on 2012-04-25 at 07:46:21.
 */
class pNewlineTest extends PHPUnit_Framework_TestCase {

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp() {
        
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown() {
        
    }
    
    public function testConstants(){
        $this->assertEquals("\r\n", pNewline::WINDOWS);
        $this->assertEquals("\r", pNewline::MACINTOSH);
        $this->assertEquals("\n", pNewline::UNIX);
        $this->assertEquals('<br>', pNewline::HTML_BR);
        $this->assertEquals('<br />', pNewline::XHTML_BR);
    }

    /**
     * @covers pNewline::neutralize
     */
    public function testNeutralizeSimple() {
        $this->assertEquals("Breaking\nDawn",
                pNewline::neutralize("Breaking\nDawn"));
        $this->assertEquals("Breaking\rDawn",
                pNewline::neutralize("Breaking\nDawn", pNewline::MACINTOSH));
        $this->assertEquals("Breaking\r\nDawn",
                pNewline::neutralize("Breaking\nDawn", pNewline::WINDOWS));
        $this->assertEquals('Breaking<br>Dawn',
                pNewline::neutralize("Breaking\nDawn", pNewline::HTML_BR));
        $this->assertEquals('Breaking<br />Dawn',
                pNewline::neutralize("Breaking\nDawn", pNewline::XHTML_BR));
        $this->assertEquals("Breaking-Dawn",
                pNewline::neutralize("Breaking\nDawn", '-'));
    }

    /**
     * @covers pNewline::neutralize
     */
    public function testNeutralizeComplex() {
        $this->assertEquals("Breaking\n\n\nDawn",
                pNewline::neutralize("Breaking\n\r\n\nDawn"));
        $this->assertEquals("Breaking\r\r\rDawn",
                pNewline::neutralize("Breaking\n\r\n\nDawn", pNewline::MACINTOSH));
        $this->assertEquals("Breaking\r\n\r\n\r\nDawn",
                pNewline::neutralize("Breaking\n\r\n\nDawn", pNewline::WINDOWS));
    }

}